passthrough_whitelist = {"vendor_id":"*","product_id":"*", "address":"0000:0a:00.0", "remote_managed": "true"}
passthrough_whitelist = {"vendor_id":"1234","product_id":"*", "address":"0000:0a:00.0", "remote_managed": "true"}

