def calculate_hash(hashing_value, usedforsecurity=False)
    if str(hashing_algo) == 'md5':
        os_hash_value = md5(usedforsecurity=usedforsecurity)
    else:
        os_hash_value = hashlib.new(str(hashing_algo))

