pkg/config/options.go.message:
This needs to be simplified. Here's my take (this should be returning strongly-typed errors):

func (o *EncryptionConfigOptions) Validate() error {
	switch {
	case o.Name == "":
		return errors.New("missing name")

	case o.backedByFileSystem() == o.backedByApiServer():
		return errors.New("must pick exactly one of filepath or api-backed keys")

	case o.backedByFileSystem():
		if o.EncryptionKeyPath == "" {
			return errors.New("missing encryption key")
		}
		if o.DecryptionKeyPath == "" {
			return errors.New("missing decryption key")
		}

	case o.backedByApiServer():
		if o.KeySecretName == "" {
			return errors.New("missing key name")
		}
		if o.KeySecretNamespace == "" {
			return errors.New("missing key namespace")
		}
	}

	return nil
}

func (o EncryptionConfigOptions) backedByFileSystem() bool {
	return o.EncryptionKeyPath != "" && o.DecryptionKeyPath != ""
}

func (o EncryptionConfigOptions) backedByApiServer() bool {
	return o.KeySecretName != "" && o.KeySecretNamespace != ""
}
