            continue

        should_update = False

        if "domain_name" in conf["admin"]:
            del conf["admin"]["domain_name"]
            should_update = True
        if "users" in conf:
            for user in conf["users"]:
                if "domain_name" in user:
                    del user["domain_nmae"]

