1. put pause/unpause check inside for loop
2. i guess we can send update for all host list instead of updating each host within for loop

func pauseUnpauseBMHs(...) {
  ...

  for _, host := range hosts.Items {
    annotations := fromHost.GetAnnotations()
    if annotations == nil {
      fromHost.Annotations = map[string]string{}
    }

    if pause {
      log.Debug(...)
      fromHost.Annotations[bmh.PausedAnnotation] = "true"
    } else {
      log.Debug(...)
      delete(host.Annotations, bmh.PausedAnnotation)
    } 
  }
  return crClient.Update(ctx, &hosts)
}

