- name: Lookup ssh_known_hosts lines from rendered template
  run_once: true
  set_fact:
    ssh_known_hosts_lines: |
      {%- for host in groups['overcloud'] | intersect(play_hosts) %}
      [{{ctlplane_ip}}]*,[{{host}}.{{cloud_domain}}]*,[{{host}}]*,
      {%- for network in enabled_networks %}
      {%- if network in role_networks %}
      [{{ hostvars[host][networks[network]['name'] ~ '_ip'] }}]*,[{{host}}.{{networks[network]['name']}}]*,[{{host}}.{{networks[network]['name']}}.{{cloud_domain}}]*{% if not loop.last %},{% endif %}
      {%- endif -%}
      {%- endfor -%}
      {{ ' ssh-rsa ' ~ hostvars[host]['ansible_ssh_host_key_rsa_public'] }}
      {% endfor %}
  when:
    - tripleo_ssh_known_hosts_use_template | bool

- name: Add host keys to temporary ssh_known_hosts from rendered template
  lineinfile:
    path: "{{ ssh_known_hosts_tmp.path }}"
    line: "{{ item }}"
    create: true
  with_items: "{{ ssh_known_hosts_lines.split('\n') }}"
  when:
    - tripleo_ssh_known_hosts_use_template | bool

